// Modal Delete

function callModal(id,route,name){
    var modal = '<div class="modal modal_area fade" id="delete_form'+id+'" tabindex="-1" role="dialog" aria-labelledby="delete_form'+id+'Label">';
    modal += '      <div class="modal-dialog" role="document">';
    modal += '          <input type="hidden" name="delete" id="delete_data" value="'+route+'">';
    modal += '              <div class="modal-content">';
    modal += '                  <div class="modal-header">';
    modal += '                      <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>';
    modal += '                          <h4 class="modal-title" id="delete_form'+id+'Label">Delete Data</h4>';
    modal += '                  </div>';
    modal += '                  <div class="modal-body">';
    modal += '                      Are you sure want to delete <b>'+name+'</b> ?';
    modal += '                  </div>';
    modal += '                  <div class="modal-footer">';
    modal += '                      <button type="button" class="btn btn-flat btn-default" data-dismiss="modal">Cancel</button>';
    modal += '                      <button onclick="destroy(event, '+id+')" type="button" class="btn btn-flat btn-danger" id="delete'+id+'">Delete</button>';
    modal += '                  </div>';
    modal += '              </div>';
    modal += '      </div>';
    modal += '   </div>';

    var $element = $('#modals');
    if(!$element.length)
        $element = $('<div id="modals"></div>').appendTo('body');

    $("#modals").append(modal)
        
}
